settings.css_code:
.product-info {
    display: flex;
    flex-direction: column;
    gap: 20px;
    padding: 0 0 20px 0;
}
.unit-price.h3 {
    text-transform: none;
}

settings.liquid_code:
{% assign variant = product.selected_or_first_available_variant %}

<div class="product-info">
  <!-- Display Price -->
  <div class="product-price h3">
 {{ variant.price | money }}
  </div>

  <!-- Calculate Unit Price (per 100g) -->
  {% if variant.price %}
    {% assign price_in_cents = variant.price | times: 100 %}  <!-- Convert price to cents -->
    {% assign unit_price_per_gram = price_in_cents | divided_by: variant.weight %}
    {% assign unit_price_per_100g = unit_price_per_gram | times: 100 %}

    <!-- Round the final result for better precision -->
    {% assign unit_price_per_100g_rounded = unit_price_per_100g | divided_by: 100.0 | round: 2 %}

    <div class="unit-price h3">
      <strong>(Pro 100gr/ml)</strong>
      {% if unit_price_per_100g_rounded > 0 %}
        {{ unit_price_per_100g_rounded | money }}
      {% endif %}
    </div>
  {% endif %}
</div>
